mapper.update:
UPDATE BOARD
SET BHIT = BHIT + 1
WHERE BID = #{param1}

UPDATE BOARD
SET BNAME = #{bName,jdbcType=VARCHAR}, BTITLE = #{bTitle,jdbcType=VARCHAR}, 
		BCONTENT = #{bContent,jdbcType=VARCHAR}
WHERE BID = #{bId,jdbcType=VARCHAR}

UPDATE BOARD_comment
SET cmstep = cmstep + 1
WHERE cmGroup = #{param1}
	AND cmstep >= #{param2}
